function Panel({id, title, icon, iconColor, children}) {
  let ref = useRef(null);
  return (
    <Disclosure id={id} className={style({...container({variant: 'emphasized'}), borderRadius: 'xl'})}>
      {({isExpanded}) => (<>
        <RACButton
          ref={ref}
          style={pressScale(ref)}
          slot="trigger"
          className={style({
            ...focusRing(),
            borderRadius: 'lg',
            backgroundColor: 'transparent',
            borderStyle: 'none',
            padding: 0,
            ...hstack(8),
            alignItems: 'center',
            textAlign: 'start',
            width: 'full',
            transition: 'default'
          })}>
          <span className={style(iconContainer({color: {iconColor: {brown: 'brown-800', silver: 'silver-1400', blue: 'blue-800', pink: 'fuchsia-700'}}, shape: 'circle'}))({iconColor})}>{icon}</span>
          <span className={style({font: 'title-lg', flexGrow: 1})}>{title}</span>
          {/* @ts-ignore */}
          <ChevronDown UNSAFE_className={style({rotate: {isExpanded: 180}, transition: 'default'})({isExpanded})} />
        </RACButton>
        <DisclosurePanel>
          <div className={style({...vstack(16), marginTop: 16})}>
            {children}
          </div>
        </DisclosurePanel>
      </>)}
    </Disclosure>
  );
}

function CircularMeter({value, maxValue}) {
  return (
    <RACMeter value={value} maxValue={maxValue} className={style({size: 70, flexShrink: 0})}>
      {({percentage}) => (
        <svg
          fill="none"
          width="100%"
          height="100%">
          <circle
            cx="50%"
            cy="50%"
            r="calc(50% - 3px)"
            strokeWidth={6}
            className={style({stroke: 'gray-300'})} />
          <circle
            cx="50%"
            cy="50%"
            r="calc(50% - 3px)"
            strokeWidth={6}
            className={style({stroke: 'blue-900', rotate: -90, transformOrigin: 'center'})}
            pathLength="100"
            strokeDasharray="100 200"
            strokeDashoffset={100 - percentage}
            strokeLinecap="round" />
          <text textAnchor="middle" dominantBaseline="middle" x="50%" y="50%" className={style({font: 'body-lg', fill: 'currentColor'})}>{value}/{maxValue}</text>
        </svg>
      )}
    </RACMeter>
  );
}
